mapper.update:
update account
set
first_name = #{firstName}
,   last_name = #{lastName}
,   dob = #{dob}
,   phone_num = #{phoneNum}
,   address = #{address}
,   description = #{description}
,   avatar_image = #{avatarImage}
,   modify_time = now()
,   modify_user_id = #{modifyUserId}
where id = #{modifyUserId}

mapper.select:
select * from account
where email = #{email}
and role = #{role}

mapper.update:
update account
set password = #{form.newPassword},
    modify_user_id = #{modifyUserId}
where id = #{modifyUserId}
