mapper.update:
update course
set
title = #{title}
,   description = #{description}
,   profile_image = #{profileImage}
,   root_price = #{rootPrice}
,   active = #{active}
,   type = #{type}
,   category_id = #{categoryId}
,   modify_time = now()
,   modify_user_id = #{modifyUserId}
where id = #{id}
