mapper.update:
update lesson
set
title = #{title}
,   chapter_id = #{chapterId}
,   description = #{description}
,   active = #{active}
,   modify_time = now()
,   modify_user_id = #{modifyUserId}
where id = #{id}

mapper.select:
select l.*
from lesson l
left join chapter ct on l.chapter_id = ct.id
left join course c on ct.course_id = c.id
where true
and c.author_account_id  = #{accountId}

    and (upper(l.title) like upper(concat('%',#{lessonTitle},'%')))


    and ct.id = #{chapterId}

limit #{limit}
offset #{offset}

select count(*) from lesson l left join chapter ct on l.chapter_id = ct.id left join course c on ct.course_id = c.id where true and c.author_account_id = #{accountId}
and (upper(l.title) like upper(concat('%',#{lessonTitle},'%')))
and ct.id = #{chapterId}
